package com.example.rxlistview.helpers;import com.example.rxlistview.Activities.HelloWorkManger;import com.example.rxlistview.model.BlogWrapper;import java.util.ArrayList;import okhttp3.MultipartBody;import okhttp3.RequestBody;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Multipart;import retrofit2.http.POST;import retrofit2.http.Part;public interface RestApiService {    @GET("feed.json")    Call<BlogWrapper> getPopularBlog();    @Multipart    @POST("upload_img_appranker.php")    Call<ArrayList<BlogWrapper>> postData(@Part MultipartBody.Part file,                                          @Part("file_name") RequestBody name,                                          @Part("file_link") RequestBody file_link);}