package com.example.rxlistview.helpers;import android.app.AlarmManager;import android.app.NotificationManager;import android.app.PendingIntent;import android.content.Context;import android.content.Intent;import androidx.annotation.NonNull;import androidx.core.app.NotificationCompat;import androidx.work.Worker;import androidx.work.WorkerParameters;import com.example.rxlistview.R;import static android.content.Context.ALARM_SERVICE;public class WorkingPeriods extends Worker {    public WorkingPeriods(@NonNull Context context, @NonNull WorkerParameters workerParams) {        super ( context, workerParams );    }    @Override    public Result doWork() {        int timeinput = Integer.parseInt("1");        Intent intent = new Intent(getApplicationContext (), MyReceiver.class);        PendingIntent pendingIntent = PendingIntent.getBroadcast( getApplicationContext(), 234324243, intent, 0);        AlarmManager alarmManager = (AlarmManager) getApplicationContext ().getSystemService( ALARM_SERVICE);        alarmManager.set(AlarmManager.RTC_WAKEUP, System.currentTimeMillis()+(timeinput * 1000), pendingIntent);        showNotification("Inducesmile", "Hello world, work completed with work manger");        return Result.success ( );    }    private void showNotification(String title, String task) {        NotificationManager notificationManager = (NotificationManager) getApplicationContext().getSystemService(Context.NOTIFICATION_SERVICE);        NotificationCompat.Builder notification = new NotificationCompat.Builder(getApplicationContext(),"inducesmile")                .setContentTitle(title)                .setContentText(task)                .setSmallIcon(R.mipmap.ic_launcher);        notificationManager.notify(1, notification.build());    }}